import React from "react";
import Message from './message';
import ioClient from 'socket.io-client';  

class Chat extends React.Component{
  constructor(props){
    super(props)
    this.socket = ioClient.connect('http://localhost:8080/');
  }

  state = {
    username: '',
    message: '',
    messageList: [],
    error: ''
  }

  componentDidMount = () =>{
    this.socket.on('receiveNewMessage', (data) => {
      this.setState({
        messageList: [...this.state.messageList, data]
      });
    })
  }

  componentWillUnmount(){
    this.socket.emit('disconnect')
  }

  sendMessage = (e) => {
    e.preventDefault();
    if(this.state.username.length !== 0 || this.state.message.length !== 0){
      this.socket.emit('sendNewMessage', {
          author: this.state.username,
          message: this.state.message
      });
      this.setState({message: ''});
      focus();
    } else {
      this.setState({
        error: "Please place name and message"
      })
    }
  }

  render(){
    return (
      <section className="container">
        <div className="card">
          <div className="card__body">
            <div className="card__title">Global Chat</div>
            <div className="messages">
              {
                this.state.messageList.map( (message, index) => {
                  return (<Message name={this.state.username} message={message} key={index}/>)
                })
              }
            </div>
          </div>
          <form className="card__form">
            <input type="text" placeholder="Username" value={this.state.username} onChange={e => this.setState({username: e.target.value})} className="form__input" required/>
            <input type="text" placeholder="Message" value={this.state.message} onChange={e => this.setState({message: e.target.value})} className="form__input" required/>
            <button onClick={this.sendMessage} className="form__button">Send</button>
          </form>
        </div>
      </section>
    );
  }
}

export default Chat;
