import React from "react";

class Chat extends React.Component{
  state = {
    username: '',
    message: '',
    messageList: []
  }

  sendMessage = (e) => {
    e.preventDefault();
    this.socket.emit('SEND_MESSAGE', {
        author: this.state.username,
        message: this.state.message
    });
    this.setState({message: ''});
  }

  render(){
    return (
      <section className="container">
        <div className="card">
          <div className="card__body">
            <div className="card__title">Global Chat</div>
            <div className="messages">
              {
                this.state.messages.map(message => {
                  return (
                    <div>{message.author}: {message.message}</div>
                  )
                })
              }
            </div>
          </div>
          <form className="card__form">
            <input type="text" placeholder="Username" value={this.state.username} onChange={e => this.setState({username: e.target.value})} className="form__input"/>
            <input type="text" placeholder="Message" value={this.state.message} onChange={e => this.setState({message: e.target.value})} className="form__input"/>
            <button onClick={this.sendMessage} className="form__button ">Send</button>
          </form>
        </div>
      </section>
    );
  }
}

export default Chat;
